package com.linker.tbook.view.component.my_page

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.findNavController
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.LinearLayoutManager
import com.linker.tbook.R
import com.linker.tbook.databinding.FragmentCartBinding
import com.linker.tbook.view.base.BaseFragment
import com.linker.tbook.view.component.my_page.myPageRecycler.cartAdapter
import com.linker.tbook.view.component.my_page.myPageRecycler.cartData

class CartFragment : BaseFragment<FragmentCartBinding> (
    FragmentCartBinding::bind, R.layout.fragment_cart
){

    // 장바구니에 담긴 상품 목록 Recyclerview
    private lateinit var cartDatas: MutableList<cartData>
    private lateinit var cartAdapter: cartAdapter


    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {

        // recyclerview 세팅
        initRecycler()

        // 임시 데이터
        addCart(cartData("data:image/png;base64,",
            "아수스 젠북",
            "아수스",
            "금요일 출고",
            "1,500,000원",
            "1개"))
        addCart(cartData("data:image/png;base64,",
            "아수스 젠북",
            "아수스",
            "금요일 출고",
            "1,500,000원",
            "1개"))
        addCart(cartData("data:image/png;base64,",
            "아수스 젠북",
            "아수스",
            "금요일 출고",
            "1,500,000원",
            "1개"))

        // 뒤로 가기 클릭
        binding.btnBack.setOnClickListener {
            goBack()
        }

        // 구매하기 클릭
        binding.btnBuy.setOnClickListener {
            view?.findNavController()?.navigate(R.id.action_cartFragment_to_orderAndPayFragment)
        }
    }

    private fun initRecycler() {
        cartAdapter = cartAdapter((
                requireContext()
                ))
        cartDatas = mutableListOf<cartData>()
        cartAdapter.datas = cartDatas
        binding.recyclerCartItem.layoutManager = LinearLayoutManager(requireContext(), LinearLayoutManager.VERTICAL, false)
        binding.recyclerCartItem.adapter = cartAdapter
        binding.recyclerCartItem.isNestedScrollingEnabled = true
    }

    private fun addCart(data: cartData) {
        cartDatas.add(data)
        cartAdapter.notifyDataSetChanged()
    }
}